import styled from 'styled-components';
import theme from '../../constants/THEME';

export const ContainerSelect =styled.div `
    position: sticky;
    top: 0px;
    display: flex;
    flex-direction:row;
    justify-content: space-around;
    align-items: center;
    padding: 2vw 0;
    background-color:${theme.colors.blue100}
`
