import styled from "styled-components";
import theme from "../../constants/THEME";

export const StyledInputSelectStyle = styled.select`
    width: 20vw;
    border: 1px solid ${theme.colors.green};
    border-radius:50px;
    margin-left:32vw;
    font-size: 1.4vw;
    padding: 1%;
    color: ${theme.colors.blue800};
    
`
